import React, {Component} from 'react';
import format from 'date-fns/format';
import parseISO from 'date-fns/parseISO';
import api from '../../../../services/api';
// import { Container } from './styles';

class Categoria extends Component {
    state = {
		alunos: [],
		idAluno: '',
	}

	handleModal = async id =>{

	} 
	async componentDidMount(){
		if(localStorage.getItem('token') == ""){
			window.location.href ='/login'
		}else{

			const response = await api.get('aluno/'+this.props.idAluno,{ headers: { Authorization: 'Bearer '+localStorage.getItem('token') } });
			response.data.forEach(alunos => {
				const teste = alunos.nascimento;
				const teste2 = teste.toString().split('T');
				const firstDate = parseISO(teste2[0]);
				alunos.nascimento = format(firstDate,"dd'/'MM'/'yyyy");
			});
            this.setState({alunos: response.data})
            console.log(response.data);
		}	

	}
	render() { 
		return (
            <div className="modal fade modalAlunos" tabindex="-1" role="dialog" aria-labelledby="myLargeModalLabel" aria-hidden="true">
                <div className="modal-dialog modal-lg">
                    <div className="modal-body">
                        <div className="modal-content">
                            <div className="modal-header">
                                <h4 className="modal-title" id="myModalLabel">{}</h4>
                                <button type="button" className="close" data-dismiss="modal" aria-hidden="true">&times;</button>
                            </div>
                            <div className="modal-body">
                                <form>
                                    <div class="form-group row">
                                        <label for="text" class="col-sm-2 col-form-label">Titulo:</label>
                                        <div class="col-sm-10">
                                            <input type="text" class="form-control" id="title" placeholder="Titulo"/>
                                        </div>
                                    </div>
                                    <div class="form-group row">
                                        <label for="description" class="col-sm-2 col-form-label">Descrição:</label>
                                        <div class="col-sm-10">
                                            <textarea class="form-control" id="description" rows="3"></textarea>
                                        </div>
                                    </div>
                                </form>
                            </div>
                            <div class="modal-footer">
                                <button type="button" class="btn btn-secondary" data-dismiss="modal">Fechar</button>
                                <button type="button" class="btn btn-primary">Salvar alterações</button>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
		);
	}
}


export default Categoria;
