import React, {Component} from 'react';

import api from '../../../../services/api';
// import { Container } from './styles';

class Alunos extends Component {
    state = {
		_id: '',
		nome: '',
        cpf: '',
        codigo: '',
		nascimento: '',
		sexo: '',
		email: '',
		telefone: '',
    }

    handleSubmit = async e =>{
		e.preventDefault();
		try{
            if(this.props._id == '') {
                await api.post('/aluno/create',{
                    nome: this.state.nome,
                    cpf: this.state.cpf,
                    codigo: this.state.codigo,
                    nascimento: this.state.nascimento,
                    sexo: this.state.sexo,
                    email: this.state.email,
                    telefone: this.state.telefone,
                },{ headers: { Authorization: 'Bearer '+localStorage.getItem('token') } });
            }else{
                const response =  await api.post('login',{
                    codigo: this.state.codigo,
                    cpf: this.state.cpf,
                });
            }     

		}catch(err){
			localStorage.setItem('token', "")
			alert("Usuario ou senha incorretos!");
		}

	}
	handleChange = e =>{
		this.setState({[e.target.name]: e.target.value})
    }

	render() { 
		return (
            <div className="modal fade modalAlunos" role="dialog" aria-labelledby="myLargeModalLabel" aria-hidden="true">
                <div className="modal-dialog modal-lg">
                    <div className="modal-body">
                        <div className="modal-content">
                            <div className="modal-header">
                                <h4 className="modal-title" id="myModalLabel">Aluno</h4>
                                <button type="button" className="close" data-dismiss="modal" aria-hidden="true">&times;</button>
                            </div>
                            <div className="modal-body">

                                <form>
                                    <input onChange={this.handleChange} type="hidden" value={this.props._id} id="_id" name="_id"></input>
                                    <div class="form-group row">
                                        <div className="col-12">
                                            <label for="text" >Nome:</label>
                                            <input onChange={this.handleChange} type="text" value={this.props.nome} class="form-control" id="nome" name="nome" placeholder="Nome"/>
                                        </div>
                                    </div>
                                    <div class="form-group row ">
                                        <div className="col-6">
                                            <label for="template-contactform-subject">Código: <small>*</small></label>
                                            <input  type="text" value={this.props.codigo} class="form-control" id="codigo" name="codigo" placeholder="Código"/>
                                        </div>

                                        <div className="col-6">
                                            <label for="sub">CPF:</label>
                                            <input onChange={this.handleChange}  type="text" value={this.props.cpf} class="form-control" id="cpf" name="cpf" placeholder="CPF"/>
                                        </div>
                                    </div>

                                    <div class="form-group row ">
                                        <div className="col-6">
                                            <label for="template-contactform-subject">Nascimento: <small>*</small></label>
                                            <input onChange={this.handleChange}  type="text" value={this.props.nascimento} class="form-control" id="nascimento" name="nascimento" placeholder="Nascimento"/>
                                        </div>

                                        <div className="col-6">
                                            <label for="sub">Sexo:</label>
                                            <input onChange={this.handleChange}  type="text" value={this.props.sexo} class="form-control" id="sexo" name="sexo" placeholder="Sexo"/>
                                        </div>
                                    </div>

                                    <div class="form-group row ">
                                        <div className="col-6">
                                            <label for="template-contactform-subject">Email: <small>*</small></label>
                                            <input onChange={this.handleChange}  type="text" value={this.props.email} class="form-control" id="email" name="email" placeholder="Email"/>
                                        </div>

                                        <div className="col-6">
                                            <label for="sub">Telefone:</label>
                                            <input onChange={this.handleChange}  type="text" value={this.props.telefone} class="form-control" id="telefone" name="telefone" placeholder="Telefone"/>
                                        </div>
                                    </div>
                                </form>
                            </div>
                            <div class="modal-footer">
                                <button type="button" class="btn btn-secondary" data-dismiss="modal">Fechar</button>
                                <button type="button" class="btn btn-primary">Salvar</button>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
		);
	}
}


export default Alunos;
