import React, {Component} from 'react';

import logoSide from '../images/logo-side.png';
import logo from '../images/logo-side@2x.png';
import api from '../services/api';
class Login extends Component{
	state = {
		codigo: '',
		cpf: '',
	};

	handleSubmit = async e =>{
		e.preventDefault();
		const response =  await api.post('login',{
			codigo,
			cpf,
		});

		localStorage.setItem('token', response.data.token)

		this.props.history.push("/admin");
	}


    render() {
        return(
            <section id="content">

			<div class="content-wrap nopadding">


				<div class="section nobg full-screen nopadding nomargin">
					<div class="container-fluid vertical-middle divcenter clearfix">

						<div class="card divcenter noborder cardLogin" >
							<div class="card-body" >
                                <div class="center">
                                    <a href="index.html"><img class="imgLogin" src={logo} alt="Canvas Logo"/></a>
                                </div>
								<form id="login-form" name="login-form" class="nobottommargin" action="#" method="post">
									<h4>Fa√ßa login para acessar o sistema</h4>

									<div class="col_full">
										<label for="login-form-username">Codigo:</label>
										<input type="text" id="login-form-username" name="login-form-username" value="" class="form-control not-dark" />
									</div>

									<div class="col_full">
										<label for="login-form-password">CPF:</label>
										<input type="password" id="login-form-password" name="login-form-password" value="" class="form-control not-dark" />
									</div>

									<div class="col_full nobottommargin">
										<button class="button button-3d button-blue nomargin" id="login-form-submit" name="login-form-submit" value="login">Login</button>
									</div>
								</form>

							</div>
						</div>


					</div>
				</div>

			</div>

		</section>
        );
    }
}

export default Login;
