import React, {Component} from 'react';

import logoSide from '../images/logo-side.png';
import logo from '../images/logo-side@2x.png';

// import { Container } from './styles';
class Header extends Component {

	async componentDidMount(){
		if(localStorage.getItem('token') == ""){
			window.location.href ='/login'
		}else{

			const response = await api.get('aluno/'+id,{ headers: { Authorization: 'Bearer '+localStorage.getItem('token') } });
			const teste = response.data.nascimento;
			const teste2 = teste.toString().split('T');
			const firstDate = parseISO(teste2[0]);
			response.data.nascimento = format(firstDate,"dd'/'MM'/'yyyy");
			this.setState({_id: response.data._id})
			this.setState({nome: response.data.nome})
			this.setState({codigo: response.data.codigo})
			this.setState({cpf: response.data.cpf})
			this.setState({nascimento: response.data.nascimento})
			this.setState({sexo: response.data.sexo})
			this.setState({email: response.data.email})
			this.setState({telefone: response.data.telefone})
		}	

	}

	render() {
		return (
			<header id="header">

					<div id="header-wrap">

						<div className="container clearfix">

							<div id="primary-menu-trigger"><i className="icon-reorder"></i></div>

							<div id="logo" className="nobottomborder">
								<a href="/" className="standard-logo" ><img src={logoSide} alt="Canvas Logo"></img></a>
								<a href="/" className="retina-logo" ><img src={logo} alt="Canvas Logo"></img></a>
							</div>

							<nav id="primary-menu">

								<ul>
									<li><a href="/"><div>Home</div></a></li>
									<li><a href="/contact"><div>Contato</div></a></li>
									<li><a href="/about"><div>Quem sou</div></a></li>
								</ul>

							</nav>

						</div>

					</div>

				</header>

		  );
	}

}

export default Header;
