import React, {Component} from 'react';
import api from '../../../services/api';
import Modal from './Components/Modal';
import format from '../../../../node_modules/date-fns';

class Categorias extends Component {
	state = {
		alunos: []
	}

	async componentDidMount(){
		const response = await api.get('aluno/list');

		this.setState({alunos: response.data})
		console.log(this.state.alunos[0].nome)
	}
	render() {
		return (
			<div className="table-responsive">
				<div className="cboth">
					<h3 className="fleft">Alunos</h3>
					<a href="#" data-toggle="modal" data-target=".modalCategory" class="button button-3d button-rounded button-amber fright">Criar Nova Categoria</a>
					<br className="cboth"></br>
				</div>
				<table id="alunos" className="table table-striped table-bordered" cellspacing="0" width="100%">
					<thead>
						<tr>
							<th>Nome</th>
							<th>Codigo</th>
							<th>CPF</th>
							<th>Data de Nascimento</th>
							<th>Sexo</th>
							<th>Email</th>
							<th>Telefone</th>
							<th></th>
						</tr>
					</thead>
					<tbody>
							{this.state.alunos.map(alunos =>(
								<tr>
									<td>{alunos.nome}</td>
									<td>{alunos.codigo}</td>
									<td>{alunos.cpf}</td>
									<td>{format(alunos.nascimento,"dd'/'MMMM'/'YYYY")}</td>
									<td>{alunos.sexo}</td>
									<td>{alunos.email}</td>
									<td>{alunos.telefone}</td>
									<td className="center">
										<a href="" data-toggle="modal" data-target=".modalAlunos"><i className="icon-pencil rightmargin10"></i></a>
										<a href=""><i className="icon-remove"></i></a>
									</td>
								</tr>
							)) }

					</tbody>
				</table>
				<Modal/>
			</div>
		);
	}
}


export default Categorias;
